const chars=">+*^#1234567890_{[~JxQzLPrOIhVSC=@&";function scrambleText(e){const s=e.dataset.original||e.textContent;e.dataset.original=s;const t=s.split("");let n=t.map(()=>"");const a=Array.from({length:t.length},(e,t)=>t).sort(()=>Math.random()-.5);let o=0,i=new Set;const r=Math.max(20,Math.min(100,Math.floor(800/t.length))),c=setInterval(()=>{if(o>=t.length){clearInterval(c),e.textContent=s;return}const r=a[o];i.add(r),o++,n[r]=t[r];for(let e=0;e<t.length;e++)i.has(e)||(t[e]===" "?n[e]=" ":n[e]=chars[Math.floor(Math.random()*chars.length)]);e.textContent=n.join("")},r)}document.addEventListener("DOMContentLoaded",()=>{document.body.style.visibility="visible",document.body.style.opacity="1",document.querySelectorAll("h1, h2, h3, h4, h5, h6, a").forEach(e=>{e.addEventListener("mouseover",()=>scrambleText(e))})})